styles.body:
* { box-sizing: border-box; }

html, body {
    margin: 0; padding: 0;
    min-width: 0; min-height: 0;
    /* 修改默认的 AMIS 加载动画和背景色: https://baidu.github.io/amis/zh-CN/style/css-vars#%E5%9B%BE%E7%89%87 */
    --Spinner-bg: url('data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPgogICAgPHBhdGggLz4KPC9zdmc+Cg==');
    --body-bg: #1d243d;
}
html, body, #app {
    width: 100%; height: 100%;
}

.loading::after {
    z-index: 10000; opacity: 1;
    transition: opacity 0.5s ease-out;
    transition-delay: 0.2s;
    pointer-events: none;

    overflow: hidden;
    position: absolute; top: 0;
    width: 100%; height: 100%;

    content: '';
    background-image: var(--Spinner-bg);
    background-color: var(--body-bg);
    background-position: center;
    background-size: 12rem;
    background-repeat: no-repeat;
}
.loading.done::after {
    opacity: 0;
}

.loading > * {
    opacity: 0;
    transition: opacity 0.5s ease-in;
}
.loading.done > * {
    opacity: 1;
}

